import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [launched, setLaunched] = useState(false);  // Î∞úÏÇ¨ ÏÉÅÌÉú
  const [price, setPrice] = useState(1.0);          // Ï¥àÍ∏∞ Í∞ÄÏßú Í∞ÄÍ≤©
  const [prevPrice, setPrevPrice] = useState(1.0);  // Ïù¥Ï†Ñ Í∞ÄÍ≤©
  const [position, setPosition] = useState(0);      // Ïö∞Ï£ºÏÑ† ÏúÑÏπò

  // Î∞úÏÇ¨ Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú
  const handleLaunch = () => {
    setLaunched(true);
  };

  // Í∞ÄÍ≤© Î≥ÄÎèô Ïãú Ïö∞Ï£ºÏÑ† Ïù¥Îèô Ìö®Í≥º
  useEffect(() => {
    if (launched) {
      const interval = setInterval(() => {
        const randomPrice = (Math.random() * 2).toFixed(2);  // 0.00 ~ 2.00 ÏÇ¨Ïù¥ Í∞ÄÏßú Í∞ÄÍ≤© ÏÉùÏÑ±
        setPrice(randomPrice);

        if (prevPrice !== null) {
          if (randomPrice > prevPrice) {
            setPosition((prev) => prev - 50);  // Í∞ÄÍ≤© ÏÉÅÏäπ Ïãú Ïö∞Ï£ºÎ°ú Ïù¥Îèô
          } else if (randomPrice < prevPrice) {
            setPosition((prev) => prev + 50);  // Í∞ÄÍ≤© ÌïòÎùΩ Ïãú ÏßÄÍµ¨Î°ú Î≥µÍ∑Ä
          }
        }
        setPrevPrice(randomPrice);
      }, 5000);  // 5Ï¥àÎßàÎã§ Í∞ÄÍ≤© Í∞±Ïã†

      return () => clearInterval(interval);  // Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú Ïù∏ÌÑ∞Î≤å Ìï¥Ï†ú
    }
  }, [launched]);

  return (
    <div className={`App ${launched ? "launched" : ""}`}>
      {/* Î≥Ñ Î∞∞Í≤Ω */}
      <div className="stars"></div>
      <div className="stars"></div>
      <div className="stars"></div>

      {/* Î∞úÏÇ¨ ÌõÑ ÌôîÎ©¥ */}
      {launched ? (
        <div className="price-display" style={{ transform: `translateY(${position}px)` }}>
          <img src="/rocket.png" alt="Rocket" className="rocket" />
          <h2>Current Price: ${price}</h2>
          <p>{position < 0 ? "üöÄ To the Moon!" : "üåç Returning to Earth!"}</p>
        </div>
      ) : (
        // Ï¥àÍ∏∞ ÌôîÎ©¥ (Î∞úÏÇ¨ Ï†Ñ)
        <header className="App-header">
          <h1>Interstellar Coin üöÄ</h1>
          <p>Are you ready to conquer the universe?</p>
          <button className="launch-button" onClick={handleLaunch}>
            Launch Now
          </button>
        </header>
      )}
    </div>
  );
}

export default App;
