import React, { useState, useEffect } from "react";
import "./App.css";  // Ïä§ÌÉÄÏùº ÌååÏùº Î∂àÎü¨Ïò§Í∏∞

// ÎîîÎ≤ÑÍπÖÏö© ÌÖçÏä§Ìä∏ Ï∂úÎ†• (ÌôîÎ©¥ ÌïòÎã®Ïóê ÌëúÏãú)
const DebugText = () => {
  return (
    <div style={{ position: 'absolute', bottom: '10px', left: '10px', color: 'white' }}>
      Debug Mode: App is running!
    </div>
  );
};

// Î©îÏù∏ App Ïª¥Ìè¨ÎÑåÌä∏
function App() {
  const [launched, setLaunched] = useState(false);
  const [price, setPrice] = useState(null);
  const [prevPrice, setPrevPrice] = useState(null);
  const [bgColor, setBgColor] = useState("#000");

  useEffect(() => {
    const fetchPrice = async () => {
      try {
        const response = await fetch(
          "https://pumpportal.fun/api/price?mint=HdgbQFcYU1aHaNwmoa1yPmeYHpBa8xrfuZRwvWpfpump"
        );
        const data = await response.json();
        const currentPrice = data.price;

        console.log("API ÏùëÎãµ ÏÑ±Í≥µ! ÌòÑÏû¨ Í∞ÄÍ≤©:", currentPrice);  // ÏΩòÏÜîÏóê API ÏùëÎãµ ÌëúÏãú

        setPrice(currentPrice);

        if (prevPrice !== null) {
          if (currentPrice > prevPrice) {
            setBgColor("#4caf50");  // Í∞ÄÍ≤© ÏÉÅÏäπ Ïãú Ï¥àÎ°ùÏÉâ
          } else if (currentPrice < prevPrice) {
            setBgColor("#ff1744");  // Í∞ÄÍ≤© ÌïòÎùΩ Ïãú Î∂âÏùÄÏÉâ
          } else {
            setBgColor("#000");     // Î≥ÄÌôî ÏóÜÏùÑ Îïå Í≤ÄÏ†ïÏÉâ Ïú†ÏßÄ
          }
        }
        setPrevPrice(currentPrice);
      } catch (error) {
        console.error("API ÏöîÏ≤≠ Ïò§Î•ò Î∞úÏÉù:", error);  // API Ïò§Î•ò Î∞úÏÉù Ïãú ÏΩòÏÜîÏóê ÌëúÏãú
      }
    };

    fetchPrice();
    const interval = setInterval(fetchPrice, 10000);

    return () => clearInterval(interval);
  }, [prevPrice]);

  const handleLaunch = () => {
    setLaunched(true);
  };

  return (
    <div className={`App ${launched ? "launched" : ""}`} style={{ backgroundColor: bgColor }}>
      <div className="stars"></div>

      <header className="App-header">
  <h1>Interstellar Coin üöÄ</h1>
  <p>Are you ready to conquer the universe?</p>

  {/* Í∞ÄÍ≤© Ï†ïÎ≥¥ Î¨¥Ï°∞Í±¥ ÌëúÏãú */}
  <p className="price">Current Price: {price ? `$${price.toFixed(4)}` : "Price data is not available."}</p>

  {/* Launch Now Î≤ÑÌäº */}
  <button className="launch-button" onClick={handleLaunch}>
    {launched ? "Launched!" : "Launch Now"}
  </button>
</header>


      {/* ÎîîÎ≤ÑÍπÖ ÌÖçÏä§Ìä∏ ÌëúÏãú */}
      <DebugText />
    </div>
  );
}

export default App;
