import React, { useState, useEffect } from "react";
import "./App.css";

const milestones = [
  "Earth",
  "Solar System Edge",
  "Alpha Centauri",
  "Milky Way",
  "Andromeda",
  "Intergalactic Space",
  "Edge of the Universe",
];

function App() {
  const [launched, setLaunched] = useState(false);
  const [price, setPrice] = useState(0.05);
  const [prevPrice, setPrevPrice] = useState(null);

  // Mock price update every 5 seconds
  useEffect(() => {
    if (launched) {
      const interval = setInterval(() => {
        setPrevPrice(price);
        const newPrice = (Math.random() * 5).toFixed(2); // Random price between 0 and 5
        setPrice(parseFloat(newPrice));
      }, 5000);
      return () => clearInterval(interval);
    }
  }, [launched, price]);

  // Determine milestone based on price
  const getMilestone = (price) => {
    if (price < 1) return milestones[0];
    if (price < 2) return milestones[1];
    if (price < 3) return milestones[2];
    if (price < 4) return milestones[3];
    if (price < 5) return milestones[4];
    if (price < 6) return milestones[5];
    return milestones[6];
  };

  const milestoneIndex = milestones.indexOf(getMilestone(price));
  const rocketPosition = (milestoneIndex / (milestones.length - 1)) * 100;

  return (
    <div className={`App ${launched ? "launched" : ""}`}>
      <div className="stars"></div>
      {!launched ? (
        <header className="App-header">
          <h1>Interstellar Coin ðŸš€</h1>
          <p>Are you ready to conquer the universe?</p>
          <button className="launch-button" onClick={() => setLaunched(true)}>
            Now
          </button>
        </header>
      ) : (
        <div className="space-scene">
          <div className="milestones">
            {milestones.map((milestone, index) => (
              <span key={index} className="milestone-label" style={{ left: `${(index / (milestones.length - 1)) * 100}%` }}>
                {milestone}
              </span>
            ))}
          </div>
          <div className="rocket" style={{ left: `${rocketPosition}%` }}>
            ðŸš€
          </div>
          <div className="price-info">
            <p>Current Price: ${price}</p>
            <p>ðŸš€ Currently at: {getMilestone(price)}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
