import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [launched, setLaunched] = useState(false);
  const [price, setPrice] = useState(1.0);
  const [prevPrice, setPrevPrice] = useState(1.0);

  // ÌñâÏÑ± Î¶¨Ïä§Ìä∏ÏôÄ Í∞ÄÍ≤© Í∏∞Ï§Ä ÏÑ§Ï†ï
  const planets = [
    { name: "Earth", price: 1 },
    { name: "Mars", price: 2 },
    { name: "Jupiter", price: 3 },
    { name: "Uranus", price: 4 },
    { name: "Pluto", price: 5 }
  ];

  const handleLaunch = () => {
    setLaunched(true); // Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú Îëê Î≤àÏß∏ ÌôîÎ©¥ÏúºÎ°ú Ï†ÑÌôò
  };

  // Í∞ÄÍ≤© Î≥ÄÌôîÎ•º ÏãúÎÆ¨Î†àÏù¥ÏÖò (3Ï¥àÎßàÎã§ Î≥ÄÎèô)
  useEffect(() => {
    if (launched) {
      const priceInterval = setInterval(() => {
        setPrevPrice(price);
        setPrice((prev) => {
          const newPrice = Math.max(0.5, (prev + (Math.random() - 0.5)).toFixed(2));
          return parseFloat(newPrice);
        });
      }, 3000);
      return () => clearInterval(priceInterval);
    }
  }, [launched, price]);

  // ÌòÑÏû¨ ÏúÑÏπòÌïú ÌñâÏÑ±ÏùÑ Î∞òÌôòÌïòÎäî Ìï®Ïàò
  const getCurrentPlanet = () => {
    let currentPlanet = planets[0];
    for (let planet of planets) {
      if (price >= planet.price) {
        currentPlanet = planet;
      }
    }
    return currentPlanet.name;
  };

  // Ïö∞Ï£ºÏÑ†Ïùò ÏúÑÏπòÎ•º Í∞ÄÍ≤©Ïóê Îî∞Îùº Í≥ÑÏÇ∞
  const getRocketPosition = () => {
    const maxPrice = planets[planets.length - 1].price;
    const positionPercent = ((price - 1) / (maxPrice - 1)) * 80; // 0% ~ 80% Î≤îÏúÑ
    return Math.max(0, Math.min(positionPercent, 80));
  };

  return (
    <div className="App">
      {/* Î≥Ñ Ïï†ÎãàÎ©îÏù¥ÏÖò */}
      <div className="stars"></div>
      <div className="stars"></div>
      <div className="stars"></div>

      {/* Ï≤´ ÌôîÎ©¥: Îü∞Ïπ≠ ÎåÄÍ∏∞ */}
      {!launched ? (
        <header className="App-header">
          <h1>Interstellar Coin üöÄ</h1>
          <p>Are you ready to conquer the universe?</p>
          <button className="launch-button" onClick={handleLaunch}>
            Launch Now
          </button>
        </header>
      ) : (
        /* Îëê Î≤àÏß∏ ÌôîÎ©¥: Ïö∞Ï£ºÏÑ† Ïù¥Îèô Î∞è Í∞ÄÍ≤© ÌëúÏãú */
        <div className="universe">
          <div className="planets">
            {planets.map((planet, index) => (
              <div key={planet.name} className="planet" style={{ left: `${index * 20 + 5}%` }}>
                {planet.name}
              </div>
            ))}
          </div>
          {/* Ïö∞Ï£ºÏÑ† ÏúÑÏπò */}
          <div className="rocket" style={{ left: `${getRocketPosition()}%` }}>üöÄ</div>

          {/* Í∞ÄÍ≤© Ï†ïÎ≥¥ */}
          <div className="price-info">
            <p>Current Price: ${price.toFixed(2)}</p>
            <p>üöÄ Currently at: {getCurrentPlanet()}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
