import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [launched, setLaunched] = useState(false);
  const [price, setPrice] = useState(1.00);  // Ï¥àÍ∏∞ Í∞ÄÏßú Í∞ÄÍ≤©
  const [prevPrice, setPrevPrice] = useState(1.00);

  const planets = [
    { name: "Earth", price: 0.5 },
    { name: "Mars", price: 1.0 },
    { name: "Jupiter", price: 2.0 },
    { name: "Uranus", price: 3.0 },
    { name: "Pluto", price: 4.0 }
  ];

  const handleLaunch = () => {
    setLaunched(true);
  };

  // Í∞ÄÏßú Îç∞Ïù¥ÌÑ∞ ÏóÖÎç∞Ïù¥Ìä∏ (Í∞ÄÍ≤© Î≥ÄÎèô ÏãúÎÆ¨Î†àÏù¥ÏÖò)
  useEffect(() => {
    if (launched) {
      const interval = setInterval(() => {
        setPrevPrice(price);
        const newPrice = parseFloat((Math.random() * 5).toFixed(2));
        setPrice(newPrice);
      }, 3000);  // 3Ï¥àÎßàÎã§ Í∞ÄÍ≤© ÏóÖÎç∞Ïù¥Ìä∏

      return () => clearInterval(interval);
    }
  }, [launched, price]);

  // ÌòÑÏû¨ Í∞ÄÍ≤©Ïóê Îî∞Îùº Ïö∞Ï£ºÏÑ† ÏúÑÏπò Í≤∞Ï†ï
  const getRocketPosition = () => {
    const priceRange = planets[planets.length - 1].price;  // ÏµúÎåìÍ∞í 4.0
    const position = (price / priceRange) * 100;  // 0% ~ 100%
    return position > 100 ? 100 : position;
  };

  // ÌòÑÏû¨ ÏúÑÏπòÌïú ÌñâÏÑ± Ï∞æÍ∏∞
  const getCurrentPlanet = () => {
    for (let i = planets.length - 1; i >= 0; i--) {
      if (price >= planets[i].price) {
        return planets[i].name;
      }
    }
    return "Earth";
  };

  return (
    <div className={`App ${launched ? "launched" : ""}`}>
      <div className="stars"></div>

      {!launched ? (
        <header className="App-header">
          <h1>Interstellar Coin üöÄ</h1>
          <p>Are you ready to conquer the universe?</p>
          <button className="launch-button" onClick={handleLaunch}>
            Launch Now
          </button>
        </header>
      ) : (
        <div className="space-container">
          {/* ÌñâÏÑ± Ïù¥Î¶Ñ */}
          <div className="planets">
            {planets.map((planet, index) => (
              <span key={index} className="planet">{planet.name}</span>
            ))}
          </div>

          {/* Ïö∞Ï£ºÏÑ† */}
          <div
            className="rocket"
            style={{ left: `${getRocketPosition()}%` }}
          >
            üöÄ
          </div>

          {/* Í∞ÄÍ≤© Ï†ïÎ≥¥ */}
          <div className="price-info">
            <p>Current Price: ${price}</p>
            <p>üöÄ Currently at: {getCurrentPlanet()}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
