import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [launched, setLaunched] = useState(false);  // Î∞úÏÇ¨ ÏÉÅÌÉú
  const [price, setPrice] = useState(1.0);          // Ï¥àÍ∏∞ Í∞ÄÏßú Í∞ÄÍ≤©
  const [prevPrice, setPrevPrice] = useState(1.0);  // Ïù¥Ï†Ñ Í∞ÄÍ≤©
  const [position, setPosition] = useState(0);      // Ïö∞Ï£ºÏÑ† ÏúÑÏπò (0~4)

  const planets = ["Earth", "Mars", "Jupiter", "Uranus", "Pluto"];

  // Î∞úÏÇ¨ Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú
  const handleLaunch = () => {
    setLaunched(true);
  };

  // Í∞ÄÍ≤© Î≥ÄÎèô Ïãú Ïö∞Ï£ºÏÑ† Ïù¥Îèô
  useEffect(() => {
    if (launched) {
      const interval = setInterval(() => {
        const randomPrice = (Math.random() * 2).toFixed(2);  // 0.00 ~ 2.00 ÏÇ¨Ïù¥ Í∞ÄÏßú Í∞ÄÍ≤© ÏÉùÏÑ±
        setPrice(randomPrice);

        if (randomPrice > prevPrice && position < planets.length - 1) {
          setPosition((prev) => prev + 1);  // Í∞ÄÍ≤© ÏÉÅÏäπ Ïãú Îã§Ïùå ÌñâÏÑ±ÏúºÎ°ú Ïù¥Îèô
        } else if (randomPrice < prevPrice && position > 0) {
          setPosition((prev) => prev - 1);  // Í∞ÄÍ≤© ÌïòÎùΩ Ïãú Ïù¥Ï†Ñ ÌñâÏÑ±ÏúºÎ°ú Î≥µÍ∑Ä
        }

        setPrevPrice(randomPrice);
      }, 5000);  // 5Ï¥àÎßàÎã§ Í∞ÄÍ≤© Í∞±Ïã†

      return () => clearInterval(interval);  // Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú Ïù∏ÌÑ∞Î≤å Ìï¥Ï†ú
    }
  }, [launched, price]);

  return (
    <div className="App">
      {/* Î≥Ñ Î∞∞Í≤Ω */}
      <div className="stars"></div>
      <div className="stars"></div>
      <div className="stars"></div>

      {/* ÌñâÏÑ± Î∞∞Ïπò */}
      {launched && (
        <div className="planets">
          {planets.map((planet, index) => (
            <div key={planet} className={`planet ${planet.toLowerCase()}`}>
              <div className="planet-name">{planet}</div>
            </div>
          ))}
        </div>
      )}

      {/* Î∞úÏÇ¨ ÌõÑ ÌôîÎ©¥ */}
      {launched ? (
        <>
          {/* Ïö∞Ï£ºÏÑ† ÏúÑÏπò */}
          <div
            className="rocket"
            style={{ transform: `translateX(${position * 20}vw)` }}
          >
            üöÄ
          </div>

          {/* Í∞ÄÍ≤© Î∞è ÌñâÏÑ± ÌëúÏãú */}
          <div className="price-display">
            <h2>Current Price: ${price}</h2>
            <p>üöÄ Currently at: {planets[position]}</p>
          </div>
        </>
      ) : (
        // Ï¥àÍ∏∞ ÌôîÎ©¥ (Î∞úÏÇ¨ Ï†Ñ)
        <header className="App-header">
          <h1>Interstellar Coin üöÄ</h1>
          <p>Are you ready to conquer the universe?</p>
          <button className="launch-button" onClick={handleLaunch}>
            Launch Now
          </button>
        </header>
      )}
    </div>
  );
}

export default App;
