import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [launched, setLaunched] = useState(false);
  const [price, setPrice] = useState(1.00);
  const [prevPrice, setPrevPrice] = useState(1.00);

  const locations = [
    { name: "Earth", price: 0.5 },
    { name: "Solar System Edge", price: 1.0 },
    { name: "Alpha Centauri", price: 2.0 },
    { name: "Milky Way", price: 3.0 },
    { name: "Andromeda", price: 4.0 },
    { name: "Intergalactic Space", price: 5.0 },
    { name: "Edge of the Universe", price: 6.0 }
  ];

  const handleLaunch = () => {
    setLaunched(true);
  };

  useEffect(() => {
    if (launched) {
      const interval = setInterval(() => {
        setPrevPrice(price);
        const newPrice = parseFloat((Math.random() * 6).toFixed(2));
        setPrice(newPrice);
      }, 3000);

      return () => clearInterval(interval);
    }
  }, [launched, price]);

  const getRocketPosition = () => {
    const maxPrice = locations[locations.length - 1].price;
    const position = (price / maxPrice) * 100;
    return position > 100 ? 100 : position;
  };

  const getCurrentLocation = () => {
    for (let i = locations.length - 1; i >= 0; i--) {
      if (price >= locations[i].price) {
        return locations[i].name;
      }
    }
    return "Earth";
  };

  return (
    <div className={`App ${launched ? "launched" : ""}`}>
      <div className="stars"></div>

      {!launched ? (
        <header className="App-header">
          <h1>Interstellar Coin ðŸš€</h1>
          <p>Are you ready to conquer the universe?</p>
          <button className="launch-button" onClick={handleLaunch}>
            Launch Now
          </button>
        </header>
      ) : (
        <div className="space-container">
          <div className="locations">
            {locations.map((loc, index) => (
              <span key={index} className="location">{loc.name}</span>
            ))}
          </div>

          <div className="rocket" style={{ left: `${getRocketPosition()}%` }}>
            ðŸš€
          </div>

          <div className="price-info">
            <p>Current Price: ${price}</p>
            <p>ðŸš€ Currently at: {getCurrentLocation()}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
