import React, { useState, useEffect } from "react";
import axios from "axios";  // axiosÎ°ú ÏΩîÏù∏ Í∞ÄÍ≤© Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
import "./App.css";  // Ïä§ÌÉÄÏùº ÌååÏùº Î∂àÎü¨Ïò§Í∏∞

function App() {
  const [launched, setLaunched] = useState(false);  // Î∞úÏÇ¨ ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [price, setPrice] = useState(null);         // ÌòÑÏû¨ ÏΩîÏù∏ Í∞ÄÍ≤© ÏÉÅÌÉú
  const [prevPrice, setPrevPrice] = useState(null); // Ïù¥Ï†Ñ Í∞ÄÍ≤© Ï†ÄÏû•
  const [bgColor, setBgColor] = useState("#000");   // Î∞∞Í≤Ω ÏÉâÏÉÅ ÏÉÅÌÉú

  // ÏΩîÏù∏ Í∞ÄÍ≤© Í∞ÄÏ†∏Ïò§Îäî Ìï®Ïàò
  useEffect(() => {
    const fetchPrice = async () => {
      try {
        const response = await axios.get(
          "https://pumpportal.fun/api/price?mint=HdgbQFcYU1aHaNwmoa1yPmeYHpBa8xrfuZRwvWpfpump"
        );
        const currentPrice = response.data.price;

        setPrice(currentPrice);

        // Ïù¥Ï†Ñ Í∞ÄÍ≤©Í≥º ÎπÑÍµêÌï¥ÏÑú Î∞∞Í≤Ω ÏÉâÏÉÅ Î≥ÄÍ≤Ω
        if (prevPrice !== null) {
          if (currentPrice > prevPrice) {
            setBgColor("#1a1a2e");  // Í∞ÄÍ≤© ÏÉÅÏäπ Ïãú Î∞ùÍ≤å
          } else if (currentPrice < prevPrice) {
            setBgColor("#000");     // Í∞ÄÍ≤© ÌïòÎùΩ Ïãú Ïñ¥Îë°Í≤å
          }
        }
        setPrevPrice(currentPrice);
      } catch (error) {
        console.error("Í∞ÄÍ≤© Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò§Îäî Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
      }
    };

    fetchPrice();  // Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä Î°úÎìúÎê† Îïå Í∞ÄÍ≤© Î∂àÎü¨Ïò§Í∏∞
    const interval = setInterval(fetchPrice, 10000);  // 10Ï¥àÎßàÎã§ Í∞ÄÍ≤© ÏóÖÎç∞Ïù¥Ìä∏

    return () => clearInterval(interval);  // Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä Ïñ∏ÎßàÏö¥Ìä∏ Îê† Îïå Ïù∏ÌÑ∞Î≤å Ï†ïÎ¶¨
  }, [prevPrice]);

  const handleLaunch = () => {
    setLaunched(true);  // Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú Î∞úÏÇ¨ ÏÉÅÌÉúÎ°ú Î≥ÄÍ≤Ω
  };

  return (
    <div className={`App ${launched ? "launched" : ""}`} style={{ backgroundColor: bgColor }}>
      
      {/* Î≥Ñ Ïï†ÎãàÎ©îÏù¥ÏÖò */}
      <div className="stars"></div>
      <div className="stars"></div>
      <div className="stars"></div>

      {/* Ìó§Îçî ÏÑπÏÖò */}
      <header className="App-header">
        <h1>Interstellar Coin üöÄ</h1>
        <p>{launched ? "Launching into space!" : "Are you ready to conquer the universe?"}</p>

        {/* ÏΩîÏù∏ Í∞ÄÍ≤© ÌëúÏãú */}
        <p>Current Price: {price ? `$${price.toFixed(4)}` : "Loading price..."}</p>

        {/* Launch Now Î≤ÑÌäº */}
        <button className="launch-button" onClick={handleLaunch}>
          {launched ? "Launched!" : "Launch Now"}
        </button>
      </header>
    </div>
  );
}

export default App;
