import React, { useState, useEffect } from "react";
import "./App.css";  // Ïä§ÌÉÄÏùº ÌååÏùº Î∂àÎü¨Ïò§Í∏∞

function App() {
  const [launched, setLaunched] = useState(false);  // Î∞úÏÇ¨ ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [price, setPrice] = useState(null);         // ÌòÑÏû¨ ÏΩîÏù∏ Í∞ÄÍ≤© ÏÉÅÌÉú
  const [prevPrice, setPrevPrice] = useState(null); // Ïù¥Ï†Ñ Í∞ÄÍ≤© Ï†ÄÏû•
  const [position, setPosition] = useState(0);      // Ïö∞Ï£ºÏÑ† ÏúÑÏπò (Í∞ÄÍ≤©Ïóê Îî∞Îùº Î≥ÄÍ≤Ω)

  // ÏΩîÏù∏ Í∞ÄÍ≤© Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    if (launched) {  // Launch Now Î≤ÑÌäºÏùÑ ÎàÑÎ•∏ ÌõÑÏóêÎßå Í∞ÄÍ≤© Í∞ÄÏ†∏Ïò§Í∏∞ ÏãúÏûë
      const fetchPrice = async () => {
        try {
          const response = await fetch(
            "https://pumpportal.fun/api/price?mint=HdgbQFcYU1aHaNwmoa1yPmeYHpBa8xrfuZRwvWpfpump"
          );
          const data = await response.json();
          const currentPrice = data.price;

          setPrice(currentPrice);

          // Í∞ÄÍ≤© Î≥ÄÌôîÏóê Îî∞Îùº Ïö∞Ï£ºÏÑ† ÏúÑÏπò Î≥ÄÍ≤Ω
          if (prevPrice !== null) {
            if (currentPrice > prevPrice) {
              setPosition((prev) => prev - 50);  // Í∞ÄÍ≤© ÏÉÅÏäπ Ïãú Ïö∞Ï£ºÎ°ú Ïù¥Îèô
            } else if (currentPrice < prevPrice) {
              setPosition((prev) => prev + 50);  // Í∞ÄÍ≤© ÌïòÎùΩ Ïãú ÏßÄÍµ¨Î°ú ÎèåÏïÑÏò¥
            }
          }
          setPrevPrice(currentPrice);
        } catch (error) {
          console.error("Í∞ÄÍ≤© Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò§Îäî Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
        }
      };

      fetchPrice();  // Ï¥àÍ∏∞ Í∞ÄÍ≤© Í∞ÄÏ†∏Ïò§Í∏∞
      const interval = setInterval(fetchPrice, 10000);  // 10Ï¥àÎßàÎã§ ÏóÖÎç∞Ïù¥Ìä∏

      return () => clearInterval(interval);  // Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú Ïù∏ÌÑ∞Î≤å Ï†úÍ±∞
    }
  }, [launched, prevPrice]);

  const handleLaunch = () => {
    setLaunched(true);  // Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú Î∞úÏÇ¨ ÏÉÅÌÉúÎ°ú Î≥ÄÍ≤Ω
  };

  return (
    <div className="App">
      <div className="stars"></div> {/* Î≥Ñ Ïï†ÎãàÎ©îÏù¥ÏÖò */}

      {/* Ï≤´ ÌôîÎ©¥ */}
      {!launched && (
        <header className="App-header">
          <h1>Interstellar Coin üöÄ</h1>
          <p>Are you ready to conquer the universe?</p>
          <button className="launch-button" onClick={handleLaunch}>
            Launch Now
          </button>
        </header>
      )}

      {/* Launch Now ÌÅ¥Î¶≠ ÌõÑ Ïö∞Ï£º ÌôîÎ©¥ */}
      {launched && (
        <div className="space-screen">
          <div className="rocket" style={{ transform: `translateY(${position}px)` }}>
            üöÄ
          </div>

          <div className="price-info">
            <h2>Current Price: {price ? `$${price.toFixed(4)}` : "Loading price..."}</h2>
            <p>{position < 0 ? "Heading deeper into space!" : "Returning to Earth!"}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
