// App.js
import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [launched, setLaunched] = useState(false);
  const [price, setPrice] = useState(1.0);
  const [prevPrice, setPrevPrice] = useState(null);

  const planets = [
    { name: "Earth", price: 1 },
    { name: "Mars", price: 2 },
    { name: "Jupiter", price: 3 },
    { name: "Uranus", price: 4 },
    { name: "Pluto", price: 5 }
  ];

  useEffect(() => {
    if (launched) {
      const interval = setInterval(() => {
        setPrevPrice(price);
        const newPrice = parseFloat((Math.random() * 5).toFixed(2));
        setPrice(newPrice);
      }, 5000);

      return () => clearInterval(interval);
    }
  }, [launched, price]);

  const handleLaunch = () => {
    setLaunched(true);
  };

  const getRocketPosition = () => {
    const currentPlanetIndex = planets.findIndex(
      (planet) => price < planet.price
    );
    const positionPercentage = currentPlanetIndex * 20; 
    return `${positionPercentage}%`;
  };

  const currentPlanet = planets.find((planet) => price < planet.price) || planets[planets.length - 1];

  return (
    <div className={`App ${launched ? "launched" : ""}`}>
      {!launched && (
        <header className="App-header">
          <h1>Interstellar Coin üöÄ</h1>
          <p>Are you ready to conquer the universe?</p>
          <button className="launch-button" onClick={handleLaunch}>
            Launch Now
          </button>
        </header>
      )}

      {launched && (
        <div className="universe">
          <div className="planets">
            {planets.map((planet, index) => (
              <div key={planet.name} className="planet">
                {planet.name}
              </div>
            ))}
          </div>

          <div
            className="rocket"
            style={{ left: getRocketPosition() }}
          >
            üöÄ
          </div>

          <div className="info">
            <p>Current Price: ${price.toFixed(2)}</p>
            <p>üåç Currently at: {currentPlanet.name}
            </p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
