import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [launched, setLaunched] = useState(false);
  const [price, setPrice] = useState(1.0);
  const [prevPrice, setPrevPrice] = useState(1.0);

  const planets = [
    { name: "Earth", price: 1 },
    { name: "Mars", price: 2 },
    { name: "Jupiter", price: 3 },
    { name: "Uranus", price: 4 },
    { name: "Pluto", price: 5 },
  ];

  const handleLaunch = () => {
    setLaunched(true);
  };

  // ê°€ê²© ë³€ë™ ì‹œë®¬ë ˆì´ì…˜ (Mock ë°ì´í„°)
  useEffect(() => {
    if (launched) {
      const interval = setInterval(() => {
        setPrevPrice(price);
        setPrice((prev) => {
          const change = (Math.random() - 0.5) * 0.5; // -0.25 ~ +0.25
          return Math.max(0, (prev + change).toFixed(2));
        });
      }, 3000);

      return () => clearInterval(interval);
    }
  }, [launched, price]);

  const currentPlanet = planets.find((planet) => price < planet.price) || planets[planets.length - 1];

  return (
    <div className="App">
      {!launched ? (
        <header className="App-header">
          <h1>Interstellar Coin ðŸš€</h1>
          <p>Are you ready to conquer the universe?</p>
          <button className="launch-button" onClick={handleLaunch}>
            Launch Now
          </button>
        </header>
      ) : (
        <div className="space">
          <div className="stars"></div>
          
          <div className="planets">
            {planets.map((planet, index) => (
              <span key={planet.name} className="planet" style={{ left: `${index * 20}%` }}>
                {planet.name}
              </span>
            ))}
          </div>

          <div className="rocket" style={{ left: `${(price / 5) * 100}%` }}>ðŸš€</div>

          <div className="info">
            <p>Current Price: ${price}</p>
            <p>ðŸš€ Currently at: {currentPlanet.name}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
