import React, { useState, useEffect } from "react";
import "./App.css";

const planets = [
    { name: "Earth", price: 0.00001 },
    { name: "Solar System Edge", price: 0.0001 },
    { name: "Alpha Centauri", price: 0.01 },
    { name: "Milky Way", price: 0.1 },
    { name: "Andromeda", price: 1 },
    { name: "Intergalactic Space", price: 5 },
    { name: "Edge of the Universe", price: 10 }
];

function App() {
  const [launched, setLaunched] = useState(false);
  const [price, setPrice] = useState(0);
  const [planet, setPlanet] = useState(planets[0]);

  useEffect(() => {
    if (launched) {
      const interval = setInterval(() => {
        // 가격 범위를 0.00001 ~ 10달러로 수정
        const minPrice = 0.00001;
        const maxPrice = 10;
        const newPrice = +(Math.random() * (maxPrice - minPrice) + minPrice).toFixed(5);
        
        setPrice(newPrice);

        // 가격에 따라 행성(항성) 업데이트 로직 수정
        const currentPlanet = planets
          .slice()
          .reverse()
          .find(p => newPrice >= p.price) || planets[0];
        
        setPlanet(currentPlanet);
      }, 2000);
      return () => clearInterval(interval);
    }
  }, [launched]);

  return (
    <div className={`App ${launched ? "launched" : ""}`}>
      <div className="stars"></div>
      {!launched ? (
        <header className="App-header">
          <h1>Interstellar Coin 🚀</h1>
          <p>Are you ready to conquer the universe?</p>
          <button className="launch-button" onClick={() => setLaunched(true)}>
            Launch Now
          </button>
        </header>
      ) : (
        <div>
          <div className="planets">
            {planets.map((p) => (
              <span key={p.name} className="planet">{p.name}</span>
            ))}
          </div>
          <div
            className="rocket"
            style={{ left: `${Math.min(Math.max((price / 10) * 100, 20), 80)}%` }}
          >
            🚀
          </div>
          <div className="price-info">
            <p>Current Price: ${price}</p>
            <p>🚀 Currently at: {planet.name}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
