import React, { useState, useEffect } from "react";
import "./App.css";

const planets = [
  { name: "Earth", price: 0 },
  { name: "Solar System Edge", price: 1 },
  { name: "Alpha Centauri", price: 2 },
  { name: "Milky Way", price: 3 },
  { name: "Andromeda", price: 4 },
  { name: "Intergalactic Space", price: 5 },
  { name: "Edge of the Universe", price: 6 }
];

function App() {
  const [launched, setLaunched] = useState(false);
  const [price, setPrice] = useState(0);
  const [planet, setPlanet] = useState(planets[0]);

  useEffect(() => {
    if (launched) {
      const interval = setInterval(() => {
        const newPrice = +(Math.random() * 6).toFixed(2);
        setPrice(newPrice);
        const currentPlanet = planets.find(p => newPrice >= p.price) || planets[0];
        setPlanet(currentPlanet);
      }, 2000);
      return () => clearInterval(interval);
    }
  }, [launched]);

  return (
    <div className={`App ${launched ? "launched" : ""}`}>
      <div className="stars"></div>
      {!launched ? (
        <header className="App-header">
          <h1>Interstellar Coin ðŸš€</h1>
          <p>Are you ready to conquer the universe?</p>
          <button className="launch-button" onClick={() => setLaunched(true)}>
            Launch
          </button>
        </header>
      ) : (
        <div>
          <div className="planets">
            {planets.map((p) => (
              <span key={p.name} className="planet">{p.name}</span>
            ))}
          </div>
          <div
            className="rocket"
            style={{ left: `${(price / 6) * 100}%` }}
          >
            ðŸš€
          </div>
          <div className="price-info">
            <p>Current Price: ${price}</p>
            <p>ðŸš€ Currently at: {planet.name}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
