import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [launched, setLaunched] = useState(false);
  const [price, setPrice] = useState(1.0);
  const [prevPrice, setPrevPrice] = useState(1.0);

  const planets = [
    { name: "Earth", price: 1 },
    { name: "Mars", price: 2 },
    { name: "Jupiter", price: 3 },
    { name: "Uranus", price: 4 },
    { name: "Pluto", price: 5 }
  ];

  const handleLaunch = () => {
    setLaunched(true);
  };

  useEffect(() => {
    if (launched) {
      const interval = setInterval(() => {
        setPrevPrice(price);
        const newPrice = Math.max(0.5, (price + (Math.random() - 0.5)).toFixed(2));
        setPrice(parseFloat(newPrice));
      }, 3000);
      return () => clearInterval(interval);
    }
  }, [launched, price]);

  const getCurrentPlanet = () => {
    let currentPlanet = planets[0];
    for (let planet of planets) {
      if (price >= planet.price) {
        currentPlanet = planet;
      }
    }
    return currentPlanet.name;
  };

  const getRocketPosition = () => {
    const maxPrice = planets[planets.length - 1].price;
    const positionPercent = ((price - 1) / (maxPrice - 1)) * 80;
    return `${Math.max(0, Math.min(positionPercent, 80))}%`;
  };

  return (
    <div className="App">
      <div className="stars"></div>
      <div className="stars"></div>
      <div className="stars"></div>

      {!launched ? (
        <header className="App-header">
          <h1>Interstellar Coin ðŸš€</h1>
          <p>Are you ready to conquer the universe?</p>
          <button className="launch-button" onClick={handleLaunch}>
            Launch Now
          </button>
        </header>
      ) : (
        <div className="universe">
          <div className="planets">
            {planets.map((planet, index) => (
              <div
                key={planet.name}
                className="planet"
                style={{ left: `${index * 20}%` }}
              >
                {planet.name}
              </div>
            ))}
          </div>

          <div className="rocket" style={{ left: getRocketPosition() }}>
            ðŸš€
          </div>

          <div className="price-info">
            <p>Current Price: ${price.toFixed(2)}</p>
            <p>ðŸš€ Currently at: {getCurrentPlanet()}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
