import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [launched, setLaunched] = useState(false);
  const [price, setPrice] = useState(1.0);
  const [prevPrice, setPrevPrice] = useState(1.0);
  const [currentPlanet, setCurrentPlanet] = useState("Earth");

  const planets = ["Earth", "Mars", "Jupiter", "Uranus", "Pluto"];

  const handleLaunch = () => {
    setLaunched(true);
  };

  useEffect(() => {
    if (launched) {
      const interval = setInterval(() => {
        const newPrice = (Math.random() * 5).toFixed(2);  // Mock price
        setPrevPrice(price);
        setPrice(newPrice);

        const planetIndex = Math.floor(newPrice);
        setCurrentPlanet(planets[planetIndex] || "Pluto");
      }, 3000); // 3Ï¥àÎßàÎã§ Í∞ÄÍ≤© ÏóÖÎç∞Ïù¥Ìä∏

      return () => clearInterval(interval);
    }
  }, [launched, price]);

  const rocketPosition = Math.min(price * 20, 80); // Í∞ÄÍ≤©Ïóê Îî∞Îùº Î°úÏºì Ïù¥Îèô Î≤îÏúÑ Ï†úÌïú

  return (
    <div className="App">
      {!launched ? (
        <header className="App-header">
          <h1>Interstellar Coin üöÄ</h1>
          <p>Are you ready to conquer the universe?</p>
          <button className="launch-button" onClick={handleLaunch}>
            Launch Now
          </button>
        </header>
      ) : (
        <div className="space">
          {/* Î≥Ñ Î∞∞Í≤Ω */}
          <div className="stars"></div>
          <div className="stars"></div>
          <div className="stars"></div>

          {/* ÌñâÏÑ± ÌëúÏãú */}
          <div className="planets">
            {planets.map((planet, index) => (
              <div
                key={planet}
                className="planet"
                style={{ left: `${index * 20 + 5}%` }}
              >
                {planet}
              </div>
            ))}
          </div>

          {/* Ïö∞Ï£ºÏÑ† */}
          <div
            className="rocket"
            style={{ transform: `translateX(${rocketPosition}vw)` }}
          >
            üöÄ
          </div>

          {/* Í∞ÄÍ≤© Î∞è ÌòÑÏû¨ ÏúÑÏπò Ï†ïÎ≥¥ */}
          <div className="price-info">
            <p>Current Price: ${price}</p>
            <p>üöÄ Currently at: {currentPlanet}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
